import { Component } from 'react';
import SimplePeer from 'simple-peer';
import './App.css';



import * as React from 'react';

export interface IAppProps {
}

export default class App extends Component<IAppProps> {
  peer: any = null;
  state = {
    input: '',
    data: ''
  }

  onChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => this.setState({ [e.target.name]: e.target.value })

  componentDidMount() {

  }

  createPeer = (init = false) => {
    this.peer = new SimplePeer({
      initiator: init,
      trickle: false
    })

    this.peer.on('error', (err: Error) => console.log('error', err));

    this.peer.on('signal', (data: any) => {
      console.log('SIGNAL', JSON.stringify(data));
      this.setState({ data: data });
    })
  }

  onSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!this.peer) return;
    this.peer.signal(JSON.parse(this.state.input))
  }

  public render() {
    return (
      <div>
        <header>
          <p>welcome to my scuffed chat</p>
        </header>
        <button>init connection</button>
        <button>join connection</button>
        <form >
          <textarea value={this.state.input} onChange={this.onChange} />
          <input type="submit" value="submit" />
        </form>
        {this.state.data &&
          <pre>
            {JSON.stringify(this.state.data, null, 2)}
          </pre>
        }
      </div>
    );
  }
}
